// Главный слайдер
const allSlides = document.querySelectorAll('.slider__sliders-item');
const allSlidesList = document.querySelector('.slider__sliders-list');
let slideHeight = allSlidesList.offsetHeight / 23 * 7;
let slideMargin = allSlidesList.offsetHeight / 23;
let slidesNumber = 0;
let slidePosition = 0;
let moveUnActive = true;
for (let i = 0; i < allSlides.length; i++) {
  slidesNumber++;
  allSlidesList.childNodes[i].classList.add('slider__sliders-item--' + i);
  allSlidesList.childNodes[i].style.top = Math.round(slidePosition) + 'px';
  slidePosition += (slideHeight + slideMargin);
}

function unActivateCheck() {
  moveUnActive = false;
}
function activateCheck() {
  moveUnActive = true;
}

function showSlide() {
  let mainSlide = document.querySelector('.slider__main-slide');
  let mainTitle = document.querySelector('.slider__main-title');
  let mainPrice = document.querySelector('.slider__main-price');
  let mainSource = document.querySelector('.slider__main-source');
  let mainImg = document.querySelector('.slider__main-img');
  for (let i = 0; i < allSlides.length; i++) {
    if (allSlidesList.childNodes[i].classList.contains('slider__sliders-item--active')) {
      let slideTitle = allSlidesList.childNodes[i].querySelector('.slider__slide-title');
      let slidePrice = allSlidesList.childNodes[i].querySelector('.slider__slide-price');
      let slideSource = allSlidesList.childNodes[i].querySelector('.slider__slide-source');
      let slideImg = allSlidesList.childNodes[i].querySelector('.slider__slide-img');
      for (let j = 0; j < allSlides.length; j++) {
        if (mainSlide.classList.contains('slider__main-slide--' + j)) {
          mainSlide.classList.remove('slider__main-slide--' + j)
        }
      }
      for (let j = 0; j < allSlides.length; j++) {
        if (allSlidesList.childNodes[i].classList.contains('slider__sliders-item--' + j)) {
          mainSlide.classList.add('slider__main-slide--' + j)
        }
      }
      if (slideTitle.innerHTML) {mainTitle.innerHTML = slideTitle.innerHTML;}
      else {mainTitle.innerHTML = '';}
      if (slidePrice) {mainPrice.innerHTML = slidePrice.innerHTML;}
      else {mainPrice.innerHTML = '';}
      if (slideSource != null) {mainSource.setAttribute('srcset', slideSource.attributes.srcset.value);}
      else {mainSource.removeAttribute('srcset');}
      if (slideImg != null) {mainImg.setAttribute('src', slideImg.attributes.src.value);}
      else {mainImg.removeAttribute('src');}
      if (allSlidesList.childNodes[i].classList.contains('slider__sliders-item--akciya')) {
        mainSlide.classList.add('slider__main-slide--akciya');
      } else {
        mainSlide.classList.remove('slider__main-slide--akciya');
      }
    }
  }
}

showSlide();

let prevButton = document.querySelector('.slider__button--prev');
function moveLast() {
  allSlidesList.firstChild.before(allSlidesList.lastChild);
  allSlidesList.firstChild.style.top = -(slideHeight + slideMargin) + 'px';
}
function removeLastActive() {
  allSlidesList.childNodes[2].classList.remove('slider__sliders-item--active');
  allSlidesList.childNodes[1].classList.add('slider__sliders-item--active');
}
function prevSlide() {
  disableButtons();
  unActivateCheck();
  moveLast();
  for (let i = 0; i < allSlides.length; i++) {
    let slidePosition = allSlidesList.childNodes[i].offsetTop;
    allSlidesList.childNodes[i].style.top = +slidePosition + slideHeight + slideMargin  + 'px';
  }
  setTimeout(removeLastActive, 400);
  setTimeout(showSlide, 400);
  setTimeout(activateCheck, 400);
  setTimeout(unableButtons, 400);
}

prevButton.addEventListener('click', prevSlide);

let nextButton = document.querySelector('.slider__button--next');
function moveFirst() {
  allSlidesList.lastChild.after(allSlidesList.firstChild);
  allSlidesList.lastChild.style.top = (slidesNumber - 1) * (slideHeight + slideMargin) + 'px';
}
function removeFirstActive() {
  allSlidesList.childNodes[0].classList.remove('slider__sliders-item--active');
  allSlidesList.childNodes[1].classList.add('slider__sliders-item--active');
}
function nextSlide() {
  disableButtons();
  unActivateCheck();
  for (let i = 0; i < allSlides.length; i++) {
    let slidePosition = allSlidesList.childNodes[i].offsetTop;
    allSlidesList.childNodes[i].style.top = +slidePosition - slideHeight - slideMargin  + 'px';
  }

  setTimeout(moveFirst, 400);
  setTimeout(removeFirstActive, 400);
  setTimeout(showSlide, 400);
  setTimeout(activateCheck, 400);
  setTimeout(unableButtons, 400);
}

nextButton.addEventListener('click', nextSlide);

